import React, { Fragment } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import ArrowButton from "./Buttons/ArrowButton";
import PageNumber from "./Buttons/PageNumber";
import { changePage } from "../../actions/articleActions";
import styles from "./styles.module.css";

const Pagination = ({ visible, actual, max, changePage }) => {
  const numbers = [...Array(visible).keys()].map(number => {
    const actualNumber = number + actual - Math.floor(visible / 2);
    if (actualNumber <= max) {
      return actualNumber;
    }
    return;
  });
  return (
    <Fragment>
      <div className={styles.container}>
        {actual > 1 && <ArrowButton direction="prev" />}
        {numbers.map(number =>
          number > 0 ? <PageNumber number={number} changePage={changePage} key={number} /> : undefined
        )}
        {actual < max && <ArrowButton direction="next" />}
      </div>
      <p className={styles.total}>Total {max} pages</p>
    </Fragment>
  );
};

function mapDispatchToProps(dispatch) {
  return {
    changePage: page => dispatch(changePage(page))
  };
}

Pagination.propTypes = {
  visible: PropTypes.number.isRequired,
  actual: PropTypes.number.isRequired,
  max: PropTypes.number.isRequired
};

export default connect(null, mapDispatchToProps)(Pagination);
