import React from "react";
import PropTypes from "prop-types";
import Image from "../Image/Image";

const ArticleDetail = ({ article }) => (
  <div>
    <Image src={article.photo_source} height="60vh" />
    {(article.author || article.date) && (
      <div className={styles.details}>
        {article.author && <Author name={article.author.name} image={article.author.image || null} />}
        {article.date && <h5 className={styles.date}>{article.date}</h5>}
      </div>
    )}
    <div className={styles.p}>
      {article.title && <h1 className={styles.title}>{article.title}</h1>}
      {article.subtitle && <h3 className={styles.subtitle}>{article.subtitle}</h3>}
    </div>
  </div>
);

ArticleDetail.propTypes = {
  article: PropTypes.element
};

export default ArticleDetail;
